# for connecting from the java application within Docker
CATALINA_OPTS="-javaagent:/usr/local/tomcat/splunk-otel-javaagent.jar -Dcom.sun.management.jmxremote -Dcom.sun.management.jmxremote.port=1099 -Dcom.sun.management.jmxremote.rmi.port=1099 -Djava.rmi.server.hostname=tomcat-with-sample-app -Dcom.sun.management.jmxremote.authenticate=false -Dcom.sun.management.jmxremote.ssl=false -Dcom.sun.management.jmxremote.local.only=false -Djava.rmi.server.useLocalHostname=false -Djava.net.preferIPv4Stack=true"
